    private void conecBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_conecBTNActionPerformed

        try {
            String ipAdress = ipTXT.getText();
            int port = Integer.parseInt(portTXT.getText());
            cliente = new Socket(ipAdress, port);
            jPanel1.setVisible(false);
            jPanel3.setVisible(true);
        } catch (IOException e) {
            JOptionPane.showMessageDialog(null, "Error " + e);
        } catch (NumberFormatException ex){
            JOptionPane.showMessageDialog(null, "Ingrese datos v√°lidos");
        }


    }//GEN-LAST:event_conecBTNActionPerformed

